// //header
@use "abstracts/variables" as *;

.header {
  min-height: 90vh;
  display: flex;
  flex-direction: row-reverse;
  align-items: center;
  justify-content: center;
  gap: 3.2rem;

  &__title {
    display: flex;
    flex-direction: column;
    text-transform: uppercase;
    line-height: 250%;
    &--main {
      font-size: 4.8rem;
      text-shadow: 0.5rem 0.5rem 0px #000;
    }

    &--second {
      font-size: 3.2rem;
      text-shadow: 0.5rem 0.5rem 0px #000;
    }
  }

  &__content {
    max-width: 800px;
    display: flex;
    flex-direction: column;
    gap: 1.6rem;
  }

  &__links {
    display: flex;
    flex-direction: column;
  }

  &__link-box {
    display: flex;
    flex-direction: column;
    overflow: visible;
    width: 50rem;
    // font-size: 8.2rem;
    // text-transform: uppercase;
    // overflow: visible;

    &--link {
      height: 110%;
      // z-index: 20;
      // width: 100%;

      // display: flex;

      // z-index: 20;
      // align-items: flex-start;
      // justify-content: flex-start;

      transition: all 0.3s;
      width: 100vw;
      max-width: 100%;
      &:hover {
        transform: translateX(0.8rem);
        filter: brightness(120%);
      }
    }
  }

  &__text {
    font-size: 2.4rem;
    color: $color-text-grey;
    line-height: 130%;
    max-width: 80%;
  }

  &__cta-box {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 1.6rem;
    * {
      line-height: 130%;
    }
  }
}

@media only screen and (max-width: 875px) {
  .header {
    flex-direction: column-reverse;
    align-items: flex-start;

    &__content {
      flex-direction: row-reverse;
    }

    &__text {
      max-width: 100%;
    }

    &__cta-box {
      width: 90%;
    }
  }
}

@media only screen and (max-width: 710px) {
  .header {
    min-height: 90vh;
    &__content {
      flex-direction: column;
    }

    &__cta-box {
      width: 100%;
      flex-direction: row;
      gap: 2.4rem;
      margin-top: 2.4rem;
    }
  }
}

@media only screen and (max-width: 450px) {
  .header {
    &__link-box {
      &--link {
        font-size: 6.4rem;
      }
    }
  }
}

@media only screen and (max-width: 300px) {
  .header {
    &__link-box {
      &--link {
        font-size: 6.4rem;
      }
    }

    &__cta-box {
      flex-direction: column;
    }
  }
}

