    font-size: 1.6rem;
    text-decoration: underline;
    transition: color 0.3s;
    &:hover {
      color: $color-primary;
    }
  }

  &__contact-item {
    display: flex;
    gap: 0.8rem;
    color: $color-white;
    align-items: center;
    font-size: 1.6rem;
  }

  &__social-box {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
