    line-height: 100%;
    margin-bottom: 0.8rem;
  }

  &__list {
    padding-left: 1.6rem;
    margin-bottom: 1.2rem;

    &--item {
      color: $color-text-grey;
      font-size: 1.6rem;
    }
  }

  &__price {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    font-weight: 900;
    line-height: 1;
    &--old {
      font-size: 1.6rem;
      text-decoration: line-through;
      color: $color-text-grey;
    }

    &--new {
      font-size: 3.6rem;
    }
  }

  &__instalment {
    font-size: 1.4rem;
    color: $color-text-grey;
    text-align: center;
    margin-bottom: 1.6rem;
  }
}

